query:
 query getEvent($eventId: ID!) {
  events {
    event(id: $eventId) {
      id
      name
      slogan
      startDate
      endDate
      venues
      {
        id
        name
        address
        city
        state
        zip
      }
      milestones {
        title
        description
        dueDate
      }
      notifications {
        id
        shouldFeature
        title
        message
        startDate
        endDate
        link
        linkText
      }
    }  
  }
}
